// svg Icons 
import PromoIcon1 from "../images/PromoIcon1.js";
import PromoIcon2 from "../images/PromoIcon2.js";
import PromoIcon3 from "../images/PromoIcon3.js";
import PromoIcon4 from "../images/PromoIcon4.js";

//FontAwesome 
import 'font-awesome/css/font-awesome.min.css';

// Menu JSON File
import menu from "../menu.json";
import { useRef } from "react";

const Home = () => {
    let deliverySection = useRef();
    let deliveryBoy= useRef();
        // Move Delivery Boy 
    // let deliverySectionTop = deliverySection.current.offsetTop; 
    //     if (window .scrollX) {
            
    //     } else {
            
        // }

    return (
        <div className="home">
            {/* Start landing */}
            <section className="landing">
                <div className="container">
                    <div className="text">
                        <h3>Eat Then Work</h3>
                        <h1>Supper delicious Burger in town!</h1>
                        <p>Food is any substance consumed to provide nutritional support for an organism.</p>
                        <a className="main-btn Booking-btn" href="reservation">Book A Table</a>
                    </div>
                    <div className="banner">
                        <div className="image">
                            <img src={require("../images/hero-banner.png")} alt="" />
                        </div>
                    </div>
                </div>
                <img className="banner-shape" src={require("../images/hero-bg-shape.png")} alt="" />
            </section>
            {/* End landing */}

            {/* Start Promo */}
            <section className="promo">
                <div className="container">
                    <div className="promo-item">
                        <PromoIcon1 />
                        <h2>Italian Pizza</h2>
                        <p>Food is any substance consumed to provide nutritional support for an organism</p>
                        <img src={require("../images/promo-1.png")} alt="Pizza" />
                    </div>
                    <div className="promo-item">
                        <PromoIcon2 />
                        <h2>Burger</h2>
                        <p>Food is any substance consumed to provide nutritional support for an organism</p>
                        <img src={require("../images/promo-2.png")} alt="Pizza" />
                    </div>
                    <div className="promo-item">
                        <PromoIcon3 />
                        <h2>French Fry</h2>
                        <p>Food is any substance consumed to provide nutritional support for an organism</p>
                        <img src={require("../images/promo-3.png")} alt="Pizza" />
                    </div>
                    <div className="promo-item">
                        <PromoIcon4 />
                        <h2>Soft Drinks</h2>
                        <p>Food is any substance consumed to provide nutritional support for an organism</p>
                        <img src={require("../images/promo-4.png")} alt="Pizza" />
                    </div>
                </div>
            </section>
            {/* End Promo */}

            {/* Start About */}
            <section id="about">
                <div className="container">
                    <div className="image">
                        <img className="red-sale-shape" src={require("../images/sale-shape-red.png")} alt="Get Up To 50% Off Now" />
                        <img src={require("../images/about-img.png")} alt="Burger, Drink and Frize" />
                    </div>
                    <div className="text">
                        <h1>
                            Caferio, Burgers, and Best Pizzas <span>in Town!</span>
                        </h1>
                        <p>The restaurants in Hangzhou also catered to many northern Chinese who had fled south from Kaifeng during the Jurchen invasion of the 1120s, 
                            while it is also known that many restaurants were run by families.
                        </p>
                        <ul>
                            <li><i className="fa-solid fa-circle-check"></i>Delicious & Healthy Foods</li>
                            <li><i className="fa-solid fa-circle-check"></i>Specific Family And Kids Zone</li>
                            <li><i className="fa-solid fa-circle-check"></i>Music & Other Facilities</li>
                            <li><i className="fa-solid fa-circle-check"></i>Fastest Food Home Delivery</li>
                        </ul>
                        <a className="main-btn" href="menu">Order Now</a>
                    </div>
                </div>
            </section>
            {/* End About */}
            {/* Start Menu */}
            <section className="menu">
                <div className="container">
                    <div className="head">
                        <h1>Our Delicious <span>Foods</span></h1>
                        <h4>Popular Dishes</h4>
                        {/* <p>Food is any substance consumed to provide nutritional support for an organism.</p> */}
                    </div>
                    <div className="items">
                        <div className="item">
                            <div className="image">
                                <span className="offer">{menu.popular[0].offer && -menu.popular[0].offer + "%"}</span>
                                <img src={menu.popular[0].img} alt="" />
                                <a href="order" className="order">Order Now</a>
                            </div>
                            <p className="cat">{menu.popular[0].category}</p>
                            <h2 className="item-name">{menu.popular[0].name}</h2>
                            {( () => {
                                if (!menu.popular[0].offer) {
                                    return (<p className="price">PRICE: <span>${menu.popular[0].price}</span></p>)
                                } else {
                                    let price = menu.popular[0].price - ((menu.popular[0].offer / 100) * menu.popular[0].price);
                                    return (<p className="price">PRICE: <span>${menu.popular[0].price}</span>  <strike>${price}</strike></p>)
                                }
                            }
                            )()}
                        </div>
                        <div className="item">
                            <div className="image">
                                <span className="offer">{menu.popular[1].offer &&  -menu.popular[1].offer + "%"}</span>
                                <img src={menu.popular[1].img} alt="" />
                                <a href="order" className="order">Order Now</a>
                            </div>
                            <p className="cat">{menu.popular[1].category}</p>
                            <h2 className="item-name">{menu.popular[1].name}</h2>
                            {( () => {
                                if (!menu.popular[1].offer) {
                                    return (<p className="price">PRICE: <span>${menu.popular[1].price}</span></p>)
                                } else {
                                    let price = menu.popular[1].price - ((menu.popular[1].offer / 100) * menu.popular[1].price);
                                    return (<p className="price">PRICE: <span>${menu.popular[1].price}</span>  <strike>${price}</strike></p>)
                                }
                            }
                            )()}
                        </div>
                        <div className="item">
                            <div className="image">
                                <span className="offer">{menu.popular[2].offer && -menu.popular[2].offer + "%"}</span>
                                <img src={menu.popular[2].img} alt="" />
                                <a href="order" className="order">Order Now</a>
                            </div>
                            <p className="cat">{menu.popular[2].category}</p>
                            <h2 className="item-name">{menu.popular[2].name}</h2>
                            {( () => {
                                if (!menu.popular[2].offer) {
                                    return (<p className="price">PRICE: <span>${menu.popular[2].price}</span></p>)
                                } else {
                                    let price = menu.popular[2].price - ((menu.popular[2].offer / 100) * menu.popular[2].price);
                                    return (<p className="price">PRICE: <span>${menu.popular[2].price}</span>  <strike>${price}</strike></p>)
                                }
                            }
                            )()}
                        </div>
                    </div>
                    <a href="menu" className="main-btn">See More</a>
                </div>
            </section>
            {/* End Menu */}

            {/* Start Cta */}
            <section className="cta">
                <div className="container">
                    <div className="text">
                        <h1>The Foodie Have Excellent Of <span>Quality Burgers!</span></h1>
                        <p>The restaurants in Hangzhou also catered to many northern Chinese who had fled south from Kaifeng during the Jurchen invasion of the 1120s, while it is also known that many restaurants were run by families.</p>
                        <a href="menu" className="main-btn">Order Now</a>
                    </div>
                    <div className="image">
                        <img className="white-sale-shape" src={require("../images/sale-shape.png")} alt="50% Off Now" />
                        <img src={require("../images/cta-banner.png")} alt="Burger" />
                    </div>
                </div>
            </section>
            {/* End Cta */}

            {/* Start Delivery */}
            <section className="delivery" ref={deliverySection}>
                <div className="container">
                    <div className="text">
                        <h1>A Moments Of Delivered On <span>Right Time</span> & Place</h1>
                        <p>The restaurants in Hangzhou also catered to many northern Chinese who had fled south from Kaifeng during the Jurchen invasion of the 1120s, while it is also known that many restaurants were run by families.</p>
                        <a href="menu" className="main-btn">Order Now</a>
                    </div>
                    <div className="image">
                        <img src={require("../images/delivery-boy.png")} alt="Delivery" ref={deliveryBoy} />
                    </div>
                </div>
            </section>
            {/* End Delivery */}

            {/* Start Blog */}
            <section className="blogs">
                <div className="container">
                    <div className="head">
                        h
                    </div>
                </div>
            </section>
            {/* End Blog */}
        </div>
    );
}

export default Home;
