import useFetch from "../components/useFetch";

const Menu = () => {
    // Fetch Menu Data 
    const {data: menu, isPending, error} = useFetch("http://localhost:3500/menu");

    return ( 
        <section className="popular-dishes">
            <div className="container">
                <div className="head">
                    <h1>Our <span>Menu</span></h1>
                </div>
                <div className="items">
                { isPending && (<p>Loading...</p>) }
                { error && (<p style={{color: "red"}}>Error: {error}</p>)}
                {
                    menu && menu.map((dish) => {
                        return (
                        <div className="item" key={dish.id}>
                            <div className="image">
                                {dish}<span className="offer">{dish.offer && -dish.offer + "%"}</span>
                                <img src={dish.img} alt="" />
                                <a href="order" className="order">Order Now</a>
                            </div>
                            <p className="cat">{dish.cat}</p>
                            <h2 className="item-name">{dish.name}</h2>
                            {( () => {
                                if (!dish.offer) {
                                    return (<p className="price">PRICE: <span>${dish.price}</span></p>)
                                } else {
                                    let price = dish.price - ((dish.offer / 100) * dish.price);
                                    return (<p className="price">PRICE: <span>&pound;{parseFloat(price).toFixed(2)}</span>  <strike>&pound;{parseFloat(dish.price).toFixed(2)}</strike></p>)
                                }
                            }
                            )()}
                            </div>
                        )
                    })
                }
                </div>
            </div>
        </section>
    );
}

export default Menu;
