import { useEffect, useRef, useState } from "react";
import { Form, useActionData, useNavigate } from "react-router-dom";

const FormComponent = (props) => {
   // Form Validation Result
   const data = useActionData();

   let fNameErr, lNameErr, adrsErr, phErr, locErr, CheckBoxesErr;
   if (data) {
      if (data.name) {
         fNameErr = {
            border: "1px solid red",
         };
         lNameErr = {
            border: "1px solid red",
         };
      }
      if (data.fName) {
         fNameErr = {
            border: "1px solid red",
         };
      }
      if (data.lName) {
         lNameErr = {
            border: "1px solid red",
         };
      }
      if (data.address) {
         adrsErr = {
            border: "1px solid red",
         };
      }
      if (data.phone) {
         phErr = {
            border: "1px solid red",
         };
      }
      if (data.location) {
         locErr = {
            border: "1px solid red",
         };
      }
      if (data.checkBox) {
         CheckBoxesErr = {
            border: "1px solid red",
         };
      }
   }

   // Handle Changes
   // Inputs
   let [numOfSmallItems, setNumOfSmallItems] = useState(1);
   let [numOfMediumItems, setNumOfMediumItems] = useState(0);
   let [numOfLargeItems, setNumOfLargeItems] = useState(0);
   // CheckBoxes
   let [smallBoxChecked, setSmallBoxChecked] = useState(true);
   let [mediumBoxChecked, setMediumBoxChecked] = useState(false);
   let [largeBoxChecked, setLargeBoxChecked] = useState(false);

   let handleInputChanges = (input, val) => {
      if (val >= 0) {
         if (input === "sm") {
            setNumOfSmallItems(val);
            if (smallBoxChecked === false) {
               setSmallBoxChecked(true);
            }
            if (val === 0) {
               setSmallBoxChecked(false);
            }
         } else if (input === "med") {
            setNumOfMediumItems(val);
            if (mediumBoxChecked === false) {
               setMediumBoxChecked(true);
            }
            if (val === 0) {
               setMediumBoxChecked(false);
            }
         } else if (input === "lg") {
            setNumOfLargeItems(val);
            if (largeBoxChecked === false) {
               setLargeBoxChecked(true);
            }
            if (val === 0) {
               setLargeBoxChecked(false);
            }
         } else {
            console.error(
               "Invalid input name arg in handleInputChanges function call"
            );
         }
      }
   };
   let handleCheckBoxChanges = (box) => {
      if (box === "small") {
         setSmallBoxChecked(!smallBoxChecked);
      } else if (box === "medium") {
         setMediumBoxChecked(!mediumBoxChecked);
      } else if (box === "large") {
         setLargeBoxChecked(!largeBoxChecked);
      } else {
         console.error(
            "Invalid input name arg in handleCheckBoxChanges function call"
         );
      }
   };

   // Calculate Total Cost In Real Time
   let totalCost;
   (() => {
      let smPrice = 0,
         medPrice = 0,
         lgPrice = 0;
      if (smallBoxChecked) {
         smPrice = props.dish.price * numOfSmallItems;
         totalCost = smPrice;
      }
      if (mediumBoxChecked) {
         medPrice = props.dish.medPrice * numOfMediumItems;
      }
      if (largeBoxChecked) {
         lgPrice = props.dish.lgPrice * numOfLargeItems;
      }
      totalCost = smPrice + medPrice + lgPrice;
      if (props.dish.offer) {
         let offer = (props.dish.offer / 100) * totalCost;
         totalCost -= offer;
      }
   })();

   // Check Form Validation
   let [formDisplaying, setFormDisplaying] = useState({ display: "block" });
   let [sucOrderDisplaying, setSucOrderDisplaying] = useState({
      display: "none",
   });
   //Navigation
   let navigate = useNavigate();
   useEffect(() => {
      if (data) {
         if (Object.keys(data).length === 0) {
            setFormDisplaying({ display: "none" });
            setSucOrderDisplaying({ display: "block" });
            setTimeout(() => {
               navigate("/");
            }, 2700);
         }
      }
   }, [data]);

   return (
      <div className="content">
         <Form
            method="post"
            action=""
            className="main-form"
            style={formDisplaying}
         >
            <div className="info">
               <h2>Info</h2>
               <label className="name">
                  Name
                  <div className="input-group">
                     <input
                        type="text"
                        name="f-name"
                        placeholder="First"
                        style={fNameErr}
                     />
                     <input
                        type="text"
                        name="l-name"
                        placeholder="Last"
                        style={lNameErr}
                     />
                  </div>
                  <p className="err-msg">
                     {data && (data.name || data.fName || data.lName)}
                  </p>
               </label>
               <label className="address">
                  Address
                  <input
                     type="text"
                     name="address"
                     placeholder="Address In Details"
                     style={adrsErr}
                  />
                  <p className="err-msg">{data && data.address}</p>
               </label>
               <label className="phone">
                  Phone
                  <input
                     type="tel"
                     name="phone"
                     placeholder="+2011 4201 4769"
                     style={phErr}
                  />
                  <p className="err-msg">{data && data.phone}</p>
               </label>
               <label className="location">
                  Your Location
                  <input type="text" name="location" style={locErr} />
                  <p className="err-msg">{data && data.location}</p>
               </label>
            </div>
            <hr />
            <div className="order-details">
               <h2>Your Order</h2>
               <p className="item-name">
                  Item: <span>{props.dish.name + " " + props.dish.cat}</span>
               </p>
               <p>Size:</p>
               <div className="order-size">
                  <label className="small">
                     <input
                        type="checkbox"
                        checked={smallBoxChecked}
                        onChange={() => handleCheckBoxChanges("small")}
                        name="checkbox-small"
                        value="small-size"
                        style={CheckBoxesErr}
                     />
                     <span>
                        Small - &pound;{parseFloat(props.dish.price).toFixed(2)}
                     </span>
                     <input
                        type="number"
                        name="num-of-small-items"
                        value={numOfSmallItems}
                        onChange={(e) =>
                           handleInputChanges("sm", +e.target.value)
                        }
                        style={CheckBoxesErr}
                     />
                  </label>
                  <label className="medium">
                     <input
                        type="checkbox"
                        checked={mediumBoxChecked}
                        onChange={() => handleCheckBoxChanges("medium")}
                        name="checkbox-medium"
                        value="medium"
                        style={CheckBoxesErr}
                     />
                     <span>
                        Medium - &pound;
                        {parseFloat(props.dish.medPrice).toFixed(2)}
                     </span>
                     <input
                        type="number"
                        name="num-of-medium-items"
                        value={numOfMediumItems}
                        onChange={(e) =>
                           handleInputChanges("med", +e.target.value)
                        }
                        style={CheckBoxesErr}
                     />
                  </label>
                  <label className="large">
                     <input
                        type="checkbox"
                        checked={largeBoxChecked}
                        onChange={() => handleCheckBoxChanges("large")}
                        name="checkbox-large"
                        value="large"
                        style={CheckBoxesErr}
                     />
                     <span>
                        Large - &pound;
                        {parseFloat(props.dish.lgPrice).toFixed(2)}
                     </span>
                     <input
                        type="number"
                        name="num-of-large-items"
                        value={numOfLargeItems}
                        onChange={(e) =>
                           handleInputChanges("lg", +e.target.value)
                        }
                        style={CheckBoxesErr}
                     />
                  </label>
               </div>
               <p className="err-msg">{data && data.checkBox}</p>
            </div>
            <hr />
            <div className="payment">
               <h2>Payment</h2>
               <p>
                  Offer:{" "}
                  <span>
                     {props.dish.offer ? props.dish.offer + "%" : "No Offer"}
                  </span>
               </p>
               <p>
                  Total Cost:{" "}
                  <span>&pound;{parseFloat(totalCost).toFixed(2)}</span>
               </p>
            </div>
            <button type="submit">Submit</button>
         </Form>
         <div className="success-order" style={sucOrderDisplaying}>
            <img src={require("../images/delivery-boy.png")} alt="delivery" />
            <h3>You will have the order soon..</h3>
         </div>
      </div>
   );
}

export const formValidation = async({request}) => {
    const data = await request.formData();

    const submission = {
        fName: data.get("f-name").replaceAll(" ", ""),
        lName: data.get("l-name").replaceAll(" ", ""),
        address: data.get("address").replaceAll(" ", ""),
        phone: data.get("phone").replaceAll(" ", ""),
        location: data.get("location").replaceAll(" ", ""),
        smallBoxChecked: data.get("checkbox-small"),
        mediumBoxChecked: data.get("checkbox-medium"),
        largeBoxChecked: data.get("checkbox-large"),
        numOfSmallItems: data.get("num-of-small-items"),
        numOfMediumItems: data.get("num-of-medium-items"),
        numOfLargeItems: data.get("num-of-large-items"),
    }

    // Handle Errors
    let errors = {};
    
    // Handle Empty Inputs
    (
        () => {
            if (submission.fName === "" && submission.lName === "") {
                errors.name = "Your Name Is Required !";
            } else if (submission.fName === "" ) {
                errors.fName = "Your First Name Is Required !";
            } else if (submission.lName === "") {
                errors.lName = "Your Last Name Is Required !";
            }
            if (submission.address === "") {
                errors.address = "Your Address Is Required !";
            }
            if (submission.phone === "") {
                errors.phone = "Your Phone Is Required !";
            }
            if (!submission.smallBoxChecked && !submission.mediumBoxChecked && !submission.smallBoxChecked) {
                errors.checkBox = "You Must Choose the size of your meal !";
            }
        }
    )()

    // Regular Expressions for checking the inputs
    const onlyStringReg = /[0-9!.,@#$%^&*(){}[\]_+=><\";:?\-~/\\|]/;
    const someSpecialCharsReg = /[!@#$%^&*+\"?*~]/;
    const phoneReg =
        /^(?:(?:\(?(?:00|\+)([1-4]\d\d|[1-9]\d?)\)?)?[\-\.\ \\\/]?)?((?:\(?\d{1,}\)?[\-\.\ \\\/]?){0,})(?:[\-\.\ \\\/]?(?:#|ext\.?|extension|x)[\-\.\ \\\/]?(\d+))?$/;
    const locationUrlReg = /(https?:\/\/)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/;

    // Handle Invalid Inputs
    (
        () => {
            if (
                (submission.fName != "" &&
                onlyStringReg.test(submission.fName)) &&
                (submission.lName != "" &&
                onlyStringReg.test(submission.lName))
            ) {
                errors.name = "Please Enter valid Names !"
            } else if (submission.fName != "" && onlyStringReg.test(submission.fName)) {
                errors.fName = "Please Enter a valid First Name !"
            } else if (submission.lName != "" && onlyStringReg.test(submission.lName)) {
                errors.lName = "Please Enter a valid Last Name !"
            }
            if (submission.address != "" && someSpecialCharsReg.test(submission.address)) {
                errors.address = "Please Enter a valid Address !"
            }
            if (submission.phone != "" && !phoneReg.test(submission.phone)) {
                errors.phone = "Please Enter a valid Phone Number !"
            }
            if (submission.location != "" && !locationUrlReg.test(submission.location)) {
                errors.location = "Please Enter a valid Location !"
            }
        }
    )()

    return errors;
}


