import { Link, useLocation } from "react-router-dom";

const Breadcrumbs = () => {
    let location = useLocation();

    let cr

    let crumbs = location.pathname.split("/")
    .filter(crumb => crumb != "")
    .map(crumb => {
        <Link to={cr}>{crumb}</Link>
    })


    
    return ( 
        <h3>{location.pathname}</h3>    
    );
}

export default Breadcrumbs;
