const Blog = () => {
    return ( 
            <section className="blogs">
                <div className="container">
                    <div className="head">
                        <h1>This Is All About <span>Foods</span></h1>
                        <h4>Latest Blog Posts</h4>
                    </div>
                    <div className="blogs-container">
                    { blogsIsPending && (<p>Loading...</p>) }
                    { blogsError && (<p style={{color: "red"}}>Error: {blogsError}</p>) }
                    {
                        blogs && blogs.map((blog) => {
                            if (blog.id <= 3) {
                                return (
                                <div className="blog" key={blog.id}>
                                    <div className="image">
                                        <span className="cat">{blog.cat}</span>
                                        <img src={blog.img} alt="" />
                                    </div>
                                    <div className="content">
                                        <div className="blog-info">
                                            <p><i className="fa fa-calendar"></i>{blog.dateAndTime}</p>
                                            <p><i className="fa-regular fa-user"></i>{blog.publisher}</p>
                                        </div>
                                        <div className="text">
                                            <Link to="blog" blogId={blog.id} className="title">{blog.blogTitle}</Link>
                                            <p>{blog.blogSubTitle}</p>
                                        </div>
                                        <Link to="blog" blogId={blog.id} className="read-more">READ MORE<i className="fa-solid fa-arrow-right"></i></Link>
                                    </div>
                                </div>
                                )
                            }
                        })
                    }
                    </div>
                </div>
            </section>
    );
}

export default Blog;
