import { useState } from "react";
import { Form, useActionData } from "react-router-dom";

const FormComponent = ({dish}) => {

    const data = useActionData();
    
    let [numOfItems, setNumOfItems] = useState(1);
    let [checked, setChecked] = useState(true);

    let handleChange = (input) => {
        if (input >= 0) {
            console.log(numOfItems);
            setNumOfItems(input);
        }
    }

    return (
       <Form method="post" action="">
          <h2>Info</h2>
          <label className="name">
             Name
             <input type="text" name="f-name" placeholder="First" />
             <input type="text" name="l-name" placeholder="Last" />
          </label>
          <label className="address">
             Address
             <input
                type="text"
                name="address"
                placeholder="Address In Details"
             />
          </label>
          <label className="location">
             Location
             <input type="url" name="location" />
          </label>
          <label className="phone">
             Phone
             <input type="tel" name="phone" />
          </label>
          <hr />
          <h2>Your Order</h2>
          <label className="item-name">
             Item
             <input type="text" disabled name="item-name" value={4} />
          </label>
          <div className="size-and-num-of-items">
             <label className="small">
                <span>Small</span>
                <input
                   type="checkbox"
                   defaultChecked={checked}
                   onChange={() => setChecked(!checked)}
                   name="checkbox-small"
                   value="55s4ds"
                />
                <input
                   type="number"
                   value={numOfItems}
                   onChange={(e) => handleChange(+e.target.value)}
                />
             </label>
             <label className="medium">
                Medium
                <input type="checkbox" name="checkbox-medium" />
                <input type="number" />
             </label>
             <label className="medium">
                Large
                <input type="checkbox" name="checkbox-large" />
                <input type="number" />
             </label>
          </div>
       </Form>
    );
}

export const formValidation = async({request}) => {
    const data = await request.formData();
    console.log(data.get("f-name"));
    return true;
}


export default FormComponent;
