import { useRef } from "react";
import { Link,  NavLink , Outlet } from "react-router-dom";

const RootLayout = () => {
    
    //Toggle Menu
    let firstSpan = useRef();
    let secondSpan = useRef();
    let thirdSpan = useRef();
    let nav = useRef();
    let toggleMenu = () => {
        firstSpan.current.classList.toggle("top-span");
        secondSpan.current.classList.toggle("middle-span");
        thirdSpan.current.classList.toggle("bottom-span");

        nav.current.classList.toggle("toggle-nav")
    };

    //Change Header Style
    let header = useRef();
    
    window.onscroll = () => {
        // Change Header Style 
        if (window.scrollY >= 10) {
            header.current.classList.add("change-header-style");
            header.current.style.backgroundColor = "white";
            header.current.style.color = "black";
            firstSpan.current.style.backgroundColor = "black"
            secondSpan.current.style.backgroundColor = "black"
            thirdSpan.current.style.backgroundColor = "black"
        } else {
            header.current.classList.remove("change-header-style");
            header.current.style.removeProperty("background-color");
            header.current.style.removeProperty("color");
            firstSpan.current.style.removeProperty("background-color");
            secondSpan.current.style.removeProperty("background-color");
            thirdSpan.current.style.removeProperty("background-color");
        }
