import { useParams } from "react-router-dom";
import useFetch from "../components/useFetch";

const BlogDetails = () => {
    // Fetch Blog Data
    let {id} = useParams();
    console.log(id);
    let {data: blog, isPending, error} = useFetch("http://localhost:4000/blogsDetails/"+ id);
    console.log(blog);

    return (
       <div className="blogs-container" style={blogsContainer}>
          {isPending && <p>Loading...</p>}
          {error && <p style={{ color: "red" }}>Error: {error}</p>}
          {blogs &&
             blogs.map((blog) => {
                return (
                   <div className="blog" key={blog.id}>
                      <div className="content">
                         <div className="blog-info">
                            <p>
                               <i className="fa fa-calendar"></i>
                               {blog.dateAndTime}
                            </p>
                            <p>
                               <i className="fa-regular fa-user"></i>
                               {blog.publisher}
                            </p>
                         </div>
                         <div className="text">
                            <Link to="blogsDetails" className="title">
                               {blog.blogTitle}
                            </Link>
                            <p>{blog.blogSubTitle}</p>
                         </div>
                         <Link to="blogsDetails" className="read-more">
                            READ MORE
                            <i className="fa-solid fa-arrow-right"></i>
                         </Link>
                      </div>
                   </div>
                );
             })}
       </div>
    );
}

export default BlogDetails;
