        }
        fetch(url)
        .then(res => {
            if (!res.ok) {
                throw Error("can not fetch the data")
            }
            return res.json();
        })
        .then(data => {
            setData(data);
            setIsPending(false);
            setError(null);
        })
        .catch(err => {
            setIsPending(false);
            setError(err.message);
        })
    }, [url])
    return {data, isPending, error}
}

export default useFetch;
