import { useEffect, useState } from "react";

const useFetch = (url) => {
    let [isPending, setIsPending] = useState(true);
    let [data, setData] = useState(n);
    let [error, setError] = useState(null);

    useEffect(() => {
        console.log("11111");
        fetch(url)
        .then(res => {
            if (!res.ok) {
                throw Error("can not fetch the data")
            }
            return res.json();
        })
        .then(data => {
            setData(data);
            setIsPending(false);
            setError(null);
        })
        .catch(err => {
            console.log("hhhh");
            setIsPending(false);
            setError(err.message);
        })
    }, [url])
    // console.log(data);

    return {data, isPending, error}
}

export default useFetch;
