import { useLocation, useParams, Link } from "react-router-dom";
import useFetch from "../components/useFetch";

const BlogDetails = () => {
    // Get Blog's id from state prop of location
    let location = useLocation();

    // Fetch Blog Data
    let {data: blog, isPending, error} = useFetch("http://localhost:4000/blogsDetails/"+ location.state);

    return (
        <div className="blog-details">
            {isPending && <p>Loading...</p>}
            {error && (<p style={{ color: "red" }}>Error: {error}</p>)}
            { blog && (
                <div className="content">
                    <h1 className="title">{blog.text.title}</h1>
                    <div className="intro">
                        <h2>{blog.text.intro.bold}</h2>
                        <p>{blog.text.intro.normal}</p>
                    </div>
                    <div className="ingredients">
                        <h2>{blog.text.ingredients.bold}</h2>
                        <dl>
                        {
                        Object.k(blog.text.ingredients).map((ingrd) => {
                            console.log(ingrd);
                            let itemNum = 1; 
                            if (ingrd == `ingredient${itemNum}`) {
                                return <dt>{blog.text.ingredients.ingrd}</dt>;
                            } else if (ingrd == `ingredient${itemNum}Details`) {
                                return <dd>{blog.text.ingredients.ingrd}</dd>;
                            }
                            itemNum++;
                        })
                        }
                        </dl>
                    </div>
                </div>
            )
            }

            {/*blog && blog.map(() => {
                    return (
                    <div className="blog" key={blog.id}>
                        <div className="content">
                            <div className="blog-info">
                                <p>
                                <i className="fa fa-calendar"></i>
                                {blog.dateAndTime}
                                </p>
                                <p>
                                <i className="fa-regular fa-user"></i>
                                {blog.publisher}
                                </p>
                            </div>
                            <div className="text">
                                <Link
                                    to={blog.blogTitle.replaceAll(" ", "-")}
                                    state={blog.id}
                                    className="title"
                                >
                                    {blog.blogTitle}
                                </Link>
                                <p>{blog.blogSubTitle}</p>
                            </div>
                            <Link
                                to={blog.blogTitle.replaceAll(" ", "-")}
                                state={blog.id}
                                className="read-more"
                            >
                                READ MORE
                                <i className="fa-solid fa-arrow-right"></i>
                            </Link>
                        </div>
                    </div>
                    );
            })*/}
        </div>
    );
}

export default BlogDetails;
