import { useState } from "react";
import { Form, useActionData } from "react-router-dom";

const FormComponent = ({dish}) => {

    const data = useActionData();
    
    let [numOfItems, setNumOfItems] = useState(1);
    let [checked, setChecked] = useState(false);

    let handleChange = (input) => {
        if (input >= 0) {
            console.log(numOfItems);
            setNumOfItems(input);
        }
    }

    return (
        <Form method="post" action="">
        <h2>Info</h2>
        <label className="name">
            Name
            <input type="text" name="f-name" placeholder="First" />
            <input type="text" name="l-name" placeholder="Last"/>
        </label>
        <label className="address">
            Address
            <input type="text" name="address" placeholder="Address In Details" />
        </label>
        <label className="location">
            Location
            <input type="url" name="location" />
        </label>
        <label className="phone">
            Phone
            <input type="tel" name="phone" />
        </label>
        <hr/>
        <h2>Your Order</h2>
        <label className="item-name">
            Item
            <input type="text" disabled name="item-name" value={4} />
        </label>
        <div className="size-and-num-of-items">
            <label className="small">
                <input type="checkbox" defaultChecked={!checked} onChange={() => setChecked(checked)}  name="checkbox-small" value="55s4ds" />
                <span>Small - price</span>
                <input type="number" value={numOfItems} onChange={(e) => handleChange(+e.target.value)} />
            </label>
            <label className="medium">
                <input type="checkbox" defaultChecked={false} onChange={() => setChecked(checked)}  name="checkbox-medium" value="55s4ds" />
                <span>Medium - price</span>
                <input type="number" />
            </label>
            <label className="large">
                <input type="checkbox" defaultChecked={false} onChange={() => setChecked(checked)}  name="checkbox-large" value="55s4ds" />
                <span>Large - price</span>
                <input type="number" />
            </label>
            <hr/>
            <div className="payment">
                <h2>Payment</h2>
                <p>Offer: </p>
                <p>Total Cost: <span></span></p>
            </div>
        </div>
        </Form>
    );
}

export const formValidation = async({request}) => {
    const data = await request.formData();
    console.log(data.get("f-name"));
    return true;
}


export default FormComponent;
