import { useRef } from "react";
import { Link,  NavLink , Outlet } from "react-router-dom";

const RootLayout = () => {
    
    //Toggle Menu
    let firstSpan = useRef();
    let nav = useRef();
    let toggleMenu = () => {
        menuSpans.current.children[0].classList.toggle("top-span");
        menuSpans.current.children[1].classList.toggle("middle-span");
        menuSpans.current.children[2].classList.toggle("bottom-span");

        nav.current.classList.toggle("toggle-nav")
    };

    //Change Header Style
    let header = useRef();
    window.onscroll = () => {
        if (window.scrollY >= 10) {
            header.current.classList.add("change-header-style");
            header.current.style.backgroundColor = "white";
            header.current.style.color = "black";
            // menuSpans.current.map(span => {
            //     span.style.backgroundColor = "black";
            // });
        } else {
            header.current.style.backgroundColor = "transparent";
        }
    }
    
    return ( 
        <div className="root-layout">
            <header ref={header}>
                <div className="container">
                    <Link to="/" className="logo"><h1>Foodie<span></span></h1></Link>
                    <nav>
                        <div className="toggle-menu" onClick={toggleMenu} ref={menuSpans}>
                            <span ref={firstSpan}></span>
                            <span ref={secondSpan}></span>
                            <span ref={thirdSpan}></span>
                        </div>
                        <ul ref={nav}>
                            <li><NavLink to="/" className="active">Home</NavLink></li>
                            <li><NavLink to="#about">About</NavLink></li>
                            <li><NavLink to="menu">Menu</NavLink></li>
                            <li><NavLink to="blog">Blog</NavLink></li>
                            <li><NavLink to="contact">Contact Us</NavLink></li>
                            <li><NavLink to="reservation" className="resrv-btn">Reservation</NavLink></li>
                        </ul>
                    </nav>
                </div>
            </header>
            <main>
                <Outlet />
            </main>
        </div>
    );
}

export default RootLayout;
