import { Link } from "react-router-dom";

import useFetch from "../components/useFetch";

const Blog = () => {
    // Fetch Menu Data 
    const {data: blogs, isPending, error} = useFetch("http://localhost:4000/blogsDetails");

    // Styles 
    let blogsContainer = {
        display: "flex",
        
    } 

    return (
       <section className="blogs">
          <div className="container">
             <div className="head">
                <h1>
                   Our <span>Blogs</span>
                </h1>
             </div>
             <div className="blogs-container" style={blogsContainer}>
                {isPending && <p>Loading...</p>}
                {error && <p style={{ color: "red" }}>Error: {error}</p>}
                {blogs &&
                   blogs.map((blog) => {
                      return (
                         <div className="blog" key={blog.id}>
                            {/* <div className="image">
                                <span className="cat">{blog.cat}</span>
                                <img src={blog.img} alt="" />
                            </div> */}
                            <div className="content">
                               <div className="blog-info">
                                  <p>
                                     <i className="fa fa-calendar"></i>
                                     {blog.dateAndTime}
                                  </p>
                                  <p>
                                     <i className="fa-regular fa-user"></i>
                                     {blog.publisher}
                                  </p>
                               </div>
                               <div className="text">
                                  <Link
                                     to="blog"
                                     blogId={blog.id}
                                     className="title"
                                  >
                                     {blog.blogTitle}
                                  </Link>
                                  <p>{blog.blogSubTitle}</p>
                               </div>
                               <Link
                                  to="blog"
                                  blogId={blog.id}
                                  className="read-more"
                               >
                                  READ MORE
                                  <i className="fa-solid fa-arrow-right"></i>
                               </Link>
                            </div>
                         </div>
                      );
                   })}
             </div>
          </div>
       </section>
    );
}

export default Blog;
