import { Link, useLocation } from "react-router-dom";

const Breadcrumbs = () => {
    let location = useLocation();

    let crumbLinks = "";

    let crumbs = location.pathname.split("/")
    .filter(crumb => crumb != "")
    .map(crumb => {
        crumbLinks += `/${crumb}`;

        return (
            <div className="crumb">
                <Link to={crumbLinks} key={cru}>{crumb}</Link>
            </div>
        )
    })


    
    return ( 
        <h3 className="breadcrumbs">{crumbs}</h3>    
    );
}

export default Breadcrumbs;
