import { useLocation } from "react-router-dom";
import useFetch from "../components/useFetch";
import Form from "../components/FormComponent";
import { useEffect, useState } from "react";

const Order = () => {
    // Get blog's id from state prop of location
    let location = useLocation();
    const [collection, setCollection] = useState(); 
    const [itemId, setItemId] = useState(); 
    useEffect(() => {
        if (location.state) {
            setCollection(location.state[0]);
            setItemId(location.state[1]);
            // Store dish's info to get it back IF the user submit and it wasn't a successful submission and refresh the page 
            // That's because we get the dish's info from state of location that pass when dish' link is clicked 
            window.sessionStorage.setItem("collection", location.state[0]);
            window.sessionStorage.setItem("itemId", location.state[1]);
        } else {
            // Get the info and set it
            setCollection(window.sessionStorage.getItem("collection"));
            setItemId(window.sessionStorage.getItem("itemId"));
        }
        
    }, [])
    // Fetch Blog Data
    let url = collection == "popular" ?
            `http://localhost:3500/popularDishes/${itemId}`
            : `http://localhost:3500/menu/${itemId}`
            let {data: dish, isPending, error} = useFetch(url)
            
    return ( 
        <section className="Order">
            <div className="container">
                {isPending && <p>Loading...</p>}
                {error && (<p style={{ color: "red" }}>Error: {error}</p>)}
                {d && <Form dish={dish} />}
            </div>
        </section>
    );
}

export default Order;
